sys_script_include.script:
var CompletamentoEmail = Class.create();
CompletamentoEmail.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getEmail: function() {

        var utilizzoRecord = new GlideRecord("sys_user");
        //assegna alla variabile utilizzoRecord tutti i record presenti in "sys_user"(utente connesso)
        utilizzoRecord.get(this.getParameter("sysparm_userID"));
        //dai record di "sys_user" prendimi tutti i parametridell'utente con l'ID connesso
        return utilizzoRecord.email + "";
        //fammi ritornare come valore solamente l'email

    },


    type: 'CompletamentoEmail'
});
